mapper.select:
SELECT *
FROM User
WHERE user_id = #{userId}
  AND user_pwd = #{userPwd}

mapper.insert:
INSERT INTO User (
	user_id
	, user_password
	, user_name
	, user_nickname
	, email
	, address
	, image
	, phone
	, exposure
) VALUES (
    #{userId}
	, #{userPassword}
	, #{userName}
	, #{userNickname}
	, #{email}
	, #{address}
	, #{image}
	, #{phone}
	, #{exposure}
)

mapper.update:
UPDATE User
SET user_pwd = #{userPwd}
  , user_name = #{userName}
  , user_nick = #{userNick}
  , email = #{email}
  , address = #{address}
  , img = #{img}
  , phone = #{phone}
  , exposure = #{exposure}
WHERE user_id = #{userId}

mapper.insert:
INSERT INTO Rival_info (
	user_seq
	, rival_seq
) VALUES (
    #{userSeq}
	, #{rivalSeq}
)

mapper.select:
SELECT user_id
FROM User
WHERE user_name = #{name}
  AND (phone = #{phoneOrEmail}
       OR email = #{phoneOrEmail})

SELECT *
FROM User
WHERE user_name = #{name}
  AND (phone = #{phoneOrEmail}
    OR email = #{phoneOrEmail})
  AND user_id = #{id}

SELECT user_seq
FROM User
WHERE email = #{email}
